export const ABACULUS = `<entryFree key="abaculus" type="hapax" id="n10"><orth lang="la" extent="full">ăbăcŭlus</orth>, <itype>i</itype>, <gen>m.</gen> <lbl>dim.</lbl> <etym>abacus</etym>, <sense level="1" n="I" id="n10.0"><hi rend="ital">a small cube or tile of colored glass for making ornamental pavements</hi>, the Gr. <foreign lang="greek">ὺβυκίσκος</foreign>, <bibl n="urn:cts:latinLit:phi0978.phi001:36:67"><author>Plin.</author> 36, 26, 67, § 199</bibl>. <cb n="ABAL"/></sense></entryFree>`;
