export const ACEO = `<entryFree key="aceo" type="main" id="n432"><orth lang="la" extent="full">ăcĕo</orth>, <itype>ui, 2</itype>, <pos>v. n.</pos> <etym>v. 2. acer</etym>, <sense level="1" n="I" id="n432.0"><hi rend="ital">to be sour.</hi> </sense><sense level="1" n="I" id="n432.1"> <usg type="style">Lit.</usg> (of wine): <cit><quote lang="la">vinum, quod neque aceat neque muceat,</quote> <bibl n="Cato Agr. 148"><author>Cato</author> R. R. 148</bibl></cit>.—</sense><sense level="1" n="II" id="n432.2"> Fig., <hi rend="ital">to be disagreeable</hi> (late Lat.): <cit><quote lang="la">mentio pectori acet,</quote> <bibl><author>Sid.</author> Ep. 7, 6</bibl></cit> <hi rend="ital">a med.</hi></sense></entryFree>`;
